// Processar resposta JSON do GPT-4 Atualizar Memoria v5.004

const results = [];

for (const item of items) {
  try {
    // A resposta do GPT-4 com json_object vem em choices[0].message.content
    const content = item.json.choices?.[0]?.message?.content || '{}';

    // Parse do JSON
    let memoryData;
    try {
      memoryData = typeof content === 'string' ? JSON.parse(content) : content;
    } catch (parseError) {
      console.error('Erro ao parsear JSON da memória:', parseError);
      // Fallback: criar estrutura vazia
      memoryData = {
        conversation_summary: '',
        new_resolved_issues: [],
        new_pending_issues: [],
        key_topics: [],
        sentiment: 'neutral'
      };
    }

    // Normalizar estrutura
    const normalized = {
      conversation_summary: memoryData.conversation_summary || '',
      new_resolved_issues: Array.isArray(memoryData.new_resolved_issues) 
        ? memoryData.new_resolved_issues 
        : [],
      new_pending_issues: Array.isArray(memoryData.new_pending_issues) 
        ? memoryData.new_pending_issues 
        : [],
      key_topics: Array.isArray(memoryData.key_topics) 
        ? memoryData.key_topics 
        : [],
      sentiment: memoryData.sentiment || 'neutral'
    };

    results.push({ json: normalized });

  } catch (error) {
    console.error('Erro ao processar memória:', error);
    // Em caso de erro, retornar estrutura vazia para não quebrar o fluxo
    results.push({
      json: {
        conversation_summary: '',
        new_resolved_issues: [],
        new_pending_issues: [],
        key_topics: [],
        sentiment: 'neutral'
      }
    });
  }
}

return results;

// =================================================================
// FUNÇÃO REFATORADA v5.003 - Normalização de Mensagens Meta
// Correções: validação de entrada, funções auxiliares, comentários
// =================================================================

/**
 * Sanitiza string para prevenir injection
 */
function sanitizeString(str, maxLength = 500) {
  if (typeof str !== 'string') return '';
  return str.trim().slice(0, maxLength);
}

/**
 * Valida e limpa número de telefone
 */
function sanitizePhoneNumber(phone) {
  if (!phone) return '';
  const cleaned = String(phone).replace(/[^0-9]/g, '');
  return cleaned.slice(0, 20); // Limite razoável
}

/**
 * Extrai informações de contato
 */
function extractContactInfo(value) {
  const contacts = value.contacts || [];
  const contact = contacts[0] || {};

  const name = sanitizeString(
    contact.profile?.name || 
    contact.profile?.Name || 
    contact.name || 
    'Contato Meta'
  );

  const waId = sanitizePhoneNumber(contact.wa_id);

  return { name, waId };
}

/**
 * Extrai texto da mensagem baseado no tipo
 */
function extractMessageText(message) {
  const messageType = message.type || 'text';
  let text = '';

  try {
    switch (messageType) {
      case 'text':
        text = message.text?.body || '';
        break;

      case 'interactive':
        const interactive = message.interactive || {};
        if (interactive.type === 'list_reply') {
          text = interactive.list_reply?.title || 
                 interactive.list_reply?.description || '';
        } else if (interactive.type === 'button_reply') {
          text = interactive.button_reply?.title || 
                 interactive.button_reply?.id || '';
        }
        break;

      case 'button':
        text = message.button?.text || '';
        break;

      case 'reaction':
        text = message.reaction?.emoji || '';
        break;

      case 'image':
      case 'video':
      case 'audio':
      case 'document':
        text = message[messageType]?.caption || 
               message[messageType]?.filename || '';
        break;

      default:
        text = '';
    }
  } catch (error) {
    console.error('Erro ao extrair texto:', error);
    text = '';
  }

  return text || `[${messageType} sem texto]`;
}

/**
 * Cria timestamp ISO válido
 */
function createTimestamp(messageTimestamp) {
  try {
    const timestampSeconds = Number(messageTimestamp || Date.now() / 1000);
    if (Number.isNaN(timestampSeconds)) {
      return new Date().toISOString();
    }
    return new Date(timestampSeconds * 1000).toISOString();
  } catch (error) {
    return new Date().toISOString();
  }
}

/**
 * Valida estrutura básica do evento
 */
function validateEvent(item) {
  if (!item || !item.json) {
    throw new Error('Item inválido: estrutura JSON ausente');
  }

  const event = item.json.event || {};
  const messages = event.messages;

  if (!Array.isArray(messages) || messages.length === 0) {
    console.warn('Evento sem mensagens válidas');
    return false;
  }

  return true;
}

// =================================================================
// PROCESSAMENTO PRINCIPAL
// =================================================================

const results = [];
const errors = [];

for (let i = 0; i < items.length; i++) {
  try {
    // Validação inicial
    if (!validateEvent(items[i])) {
      continue;
    }

    const base = items[i].json || {};
    const config = base.config || {};
    const value = base.event || {};
    const messages = value.messages || [];

    // Configurar metadados base
    const metaBase = { ...(base.meta || {}) };
    metaBase.environment = config.meta_env || metaBase.environment || 'sandbox';
    metaBase.provider = 'meta';
    metaBase.version = 'v5.003';

    // Extrair informações de contato
    const { name: contactName, waId } = extractContactInfo(value);

    // Processar cada mensagem
    for (const message of messages) {
      try {
        const senderWaId = sanitizePhoneNumber(message.from || waId);

        if (!senderWaId) {
          console.warn('Mensagem sem sender válido, pulando...');
          continue;
        }

        const isoTimestamp = createTimestamp(message.timestamp);
        const messageType = sanitizeString(message.type || 'text', 50);
        const text = sanitizeString(extractMessageText(message), 4000);

        // Metadados da mensagem
        const meta = { ...metaBase };
        meta.client_mask = senderWaId.slice(-4);
        meta.route = 'meta_inbound';
        meta.processed_at = new Date().toISOString();

        // Objeto normalizado
        const normalized = {
          sender: senderWaId,
          type: messageType,
          text: text,
          timestamp: isoTimestamp,
          message_id: sanitizeString(message.id || '', 100),
          context_id: sanitizeString(message.context?.id || '', 100),
          raw: message, // Manter raw para debug
          config,
          meta,
          client_id: senderWaId,
        };

        results.push({
          json: {
            client_id: senderWaId,
            client_name: contactName,
            message_content: text,
            message_type: messageType,
            message_id: normalized.message_id,
            timestamp: isoTimestamp,
            direction: 'inbound',
            channel: 'whatsapp:meta',
            normalized,
            meta,
            config,
            event: value,
          },
        });

      } catch (messageError) {
        errors.push({
          stage: 'message_processing',
          message_id: message.id,
          error: messageError.message
        });
        console.error('Erro ao processar mensagem:', messageError);
      }
    }

  } catch (itemError) {
    errors.push({
      stage: 'item_processing',
      item_index: i,
      error: itemError.message
    });
    console.error('Erro ao processar item:', itemError);
  }
}

// Log de erros para monitoramento
if (errors.length > 0) {
  console.error('Total de erros:', errors.length, errors);
}

// Se não há resultados válidos, retorna erro
if (results.length === 0) {
  throw new Error('Nenhuma mensagem válida foi processada');
}

return results;

const results = [];

