// ✅ VALIDAÇÃO HMAC SHA256 - Meta WhatsApp
// crypto já está disponível globalmente no n8n - NÃO importar!

const items = $input.all();

return items.map(item => {
  const config = item.json.config;
  const rawBody = item.json.raw_body;
  const headerSignature = item.json.headers['x-hub-signature-256'];

  // Validações básicas
  if (!config?.app_secret) {
    return {
      json: {
        ...item.json,
        signature: {
          valid: false,
          reason: 'META_APP_SECRET não configurado',
          header: headerSignature || 'missing',
          expected: null
        }
      }
    };
  }

  if (!rawBody) {
    return {
      json: {
        ...item.json,
        signature: {
          valid: false,
          reason: 'raw_body ausente',
          header: headerSignature || 'missing',
          expected: null
        }
      }
    };
  }

  if (!headerSignature) {
    return {
      json: {
        ...item.json,
        signature: {
          valid: false,
          reason: 'Header X-Hub-Signature-256 ausente',
          header: 'missing',
          expected: null
        }
      }
    };
  }

  // Calcular HMAC SHA256
  const hmac = crypto.createHmac('sha256', config.app_secret);
  hmac.update(rawBody, 'utf8');
  const expectedSignature = 'sha256=' + hmac.digest('hex');

  // Comparação segura (timing-safe)
  const valid = crypto.timingSafeEqual(
    Buffer.from(headerSignature),
    Buffer.from(expectedSignature)
  );

  return {
    json: {
      ...item.json,
      signature: {
        valid: valid,
        reason: valid ? 'Assinatura válida' : 'Assinatura inválida - possível tentativa de spoofing',
        header: headerSignature,
        expected: expectedSignature
      }
    }
  };
});

return items.map(item => {
  const meta = item.json.meta || {};
  meta.http_status = 401;
  meta.route = 'meta_signature_invalid';
  meta.success = false;
  meta.response = { status: 'error', reason: 'invalid_signature' };
  item.json.meta = meta;
  return { json: item.json };
});

const results = [];

for (const item of items) {
  const statuses = item.json.event?.statuses || [];
  if (!statuses.length) {
    const meta = item.json.meta || {};
    meta.route = 'meta_status_empty';
    meta.http_status = 200;
    meta.success = true;
    meta.response = { status: 'ignored' };
    results.push({ json: { meta } });
    continue;
  }

  for (const status of statuses) {
    const meta = { ...(item.json.meta || {}) };
    meta.route = 'meta_status';
    meta.http_status = 200;
    meta.success = status.status !== 'failed';
    meta.response = {
      status: status.status,
      message_id: status.id || null,
      conversation_id: status.conversation?.id || null,
      timestamp: status.timestamp || null,
    };
    if (status.errors?.length) {
      meta.error = status.errors[0].code || 'status_error';
      meta.response.error = status.errors[0];
      meta.success = false;
    } else {
      delete meta.error;
    }

    results.push({ json: { meta, status } });
  }
}

return results;

return items.map(item => {
  const meta = item.json.meta || {};
  meta.route = 'meta_unknown_event';
  meta.http_status = 200;
  meta.success = true;
  meta.response = { status: 'ignored', message: 'event-type-not-handled' };
  item.json.meta = meta;
  return { json: item.json };
});

const inputMain = this.getInputData(0);
const inputContext = this.getInputData(1);
const outputs = [];

for (let index = 0; index < inputMain.length; index += 1) {
  const gptItem = inputMain[index];
  const contextItem = inputContext?.[index] || { json: {} };
  const normalized = contextItem.json?.normalized || contextItem.json || {};
  const config = normalized.config || {};
  const meta = { ...(normalized.meta || {}) };

  const gptChoice = gptItem.json?.choices?.[0]?.message?.content || '';
  const cleanedText = (gptChoice || '').trim();

  const templateHint = normalized.meta?.outbound_template || gptItem.json?.template || null;
  const responseType = templateHint?.name || templateHint?.id ? 'template' : 'text';

  const requestBody = {
    messaging_product: 'whatsapp',
    to: normalized.sender || normalized.client_id || '',
  };

  if (normalized.message_id) {
    requestBody.context = { message_id: normalized.message_id };
  }

  if (responseType === 'template') {
    const templateName = templateHint.name || templateHint.id;
    const templateLanguage = templateHint.language || config.template_language || 'pt_BR';
    const templateComponents = templateHint.components || (templateHint.params
      ? [
          {
            type: 'body',
            parameters: templateHint.params.map(param => ({ type: 'text', text: param })),
          },
        ]
      : undefined);

    requestBody.type = 'template';
    requestBody.template = {
      name: templateName,
      language: { code: templateLanguage },
    };
    if (templateComponents) {
      requestBody.template.components = templateComponents;
    }
  } else {
    requestBody.type = 'text';
    requestBody.text = {
      preview_url: false,
      body: cleanedText || '...'
    };
  }

  const graphVersion = config.graph_version || 'v20.0';
  const phoneNumberId = config.phone_number_id || '';
  const baseUrl = config.base_url || `https://graph.facebook.com/${graphVersion}`;

  const outbound = {
    type: responseType,
    environment: config.meta_env || meta.environment || 'sandbox',
    request: {
      method: 'POST',
      url: `${baseUrl}/${phoneNumberId}/messages`,
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${config.access_token || ''}`,
      },
      body: requestBody,
    },
  };

  meta.route = responseType === 'template' ? 'meta_prepare_template' : 'meta_prepare_text';
  meta.response = { status: 'queued' };

  outputs.push({ json: { outbound, meta, normalized, config } });
}

return outputs;

const wait = (ms) => new Promise(resolve => setTimeout(resolve, ms));
const maxRetries = 2;

const results = [];

for (const item of items) {
  const outbound = item.json.outbound || {};
  const request = outbound.request || {};
  const meta = { ...(item.json.meta || {}) };
  const normalized = item.json.normalized || {};

  if (!request.url || !request.method || !request.headers?.Authorization) {
    meta.http_status = 500;
    meta.success = false;
    meta.response = { status: 'error', message: 'missing_meta_credentials' };
    meta.error = 'missing_meta_credentials';
    results.push({ json: { meta, outbound, normalized, delivery: { success: false, statusCode: 0, retries: 0 } } });
    continue;
  }

  let retries = 0;
  let success = false;
  let statusCode = 0;
  let body = null;
  let errorCategory = null;

  while (true) {
    let shouldRetry = false;
    try {
      const response = await this.helpers.httpRequest({
        method: request.method,
        url: request.url,
        headers: request.headers,
        body: request.body,
        json: true,
        resolveWithFullResponse: true,
      });
      statusCode = response.statusCode;
      body = response.body;
      if (statusCode >= 500) {
        errorCategory = 'server';
        shouldRetry = retries < maxRetries;
      } else if (statusCode >= 400) {
        errorCategory = 'client';
        shouldRetry = false;
      } else {
        success = true;
      }
    } catch (error) {
      statusCode = error.statusCode || error.response?.statusCode || 500;
      body = error.response?.body || error.message;
      errorCategory = statusCode >= 500 ? 'server' : 'client';
      shouldRetry = errorCategory === 'server' && retries < maxRetries;
    }

    if (success || !shouldRetry) {
      break;
    }

    retries += 1;
    const backoffMs = Math.min(10000, Math.pow(2, retries) * 1000);
    await wait(backoffMs);
  }

  const httpStatus = success ? statusCode || 200 : statusCode || 502;
  meta.http_status = httpStatus;
  meta.success = success;
  meta.route = outbound.type === 'template' ? 'meta_send_template' : 'meta_send_text';
  meta.response = success
    ? { status: 'sent', provider_status: statusCode, message_id: body?.messages?.[0]?.id || null }
    : { status: 'error', provider_status: statusCode, category: errorCategory, details: body?.error || body };

  if (!success) {
    meta.error = errorCategory === 'server' ? 'meta_server_error' : 'meta_client_error';
  } else {
    delete meta.error;
  }

  results.push({ json: { meta, outbound, normalized, delivery: { success, statusCode, body, retries, errorCategory } } });
}

return results;

return items.map(item => {
  const meta = item.json.meta || {};
  const startAt = meta.start_at || Date.now();
  meta.latency_ms = Date.now() - startAt;
  meta.provider = meta.provider || 'meta';
  if (!meta.http_status) {
    meta.http_status = 200;
  }
  if (!meta.response) {
    meta.response = { status: meta.success === false ? 'error' : 'ok' };
  }
  item.json = { meta };
  return item;
});

return items.map(item => {
  const meta = item.json.meta || {};
  const log = {
    timestamp: new Date().toISOString(),
    route: meta.route || 'unknown',
    environment: meta.environment || 'sandbox',
    provider: meta.provider || 'meta',
    status: meta.http_status || 200,
    success: meta.success !== false,
    latency_ms: meta.latency_ms || 0,
  };
  if (meta.client_mask) {
    log.client_mask = meta.client_mask;
  }
  if (meta.error) {
    log.error = meta.error;
  }
  if (meta.response?.provider_status) {
    log.provider_status = meta.response.provider_status;
  }
  if (meta.response?.message_id) {
    log.message_id = meta.response.message_id;
  }
  console.log('[meta-metrics]', JSON.stringify(log));
  return { json: { meta } };
});

